use crate::common;

use polars::prelude::*;
use polars_excel_writer::PolarsExcelWriter;
use rust_xlsxwriter::{Table, XlsxError};

// Compare output against target Excel file using PolarsExcelWriter.
fn create_new_xlsx_file_1(filename: &str) -> Result<(), XlsxError> {
    let df: DataFrame = df!(
        "Column1" => &["Foo", "Foo", "Foo"],
        "Column2" => &["Bar", "Bar", "Bar"],
    )?;

    let mut excel_writer = PolarsExcelWriter::new();
    excel_writer.set_header(false);
    excel_writer.set_autofit(true);

    excel_writer.write_dataframe(&df)?;
    excel_writer.save(filename)?;

    Ok(())
}

// Compare using PolarsExcelWriter and set_table().
fn create_new_xlsx_file_2(filename: &str) -> Result<(), XlsxError> {
    let df: DataFrame = df!(
        "Column1" => &["Foo", "Foo", "Foo"],
        "Column2" => &["Bar", "Bar", "Bar"],
    )?;

    let mut excel_writer = PolarsExcelWriter::new();
    let table = Table::new().set_header_row(false);

    excel_writer.set_table(&table);
    excel_writer.set_autofit(true);

    excel_writer.write_dataframe(&df)?;
    excel_writer.save(filename)?;

    Ok(())
}

#[test]
fn dataframe_write_excel02_1() {
    let test_runner = common::TestRunner::new()
        .set_name("dataframe02")
        .set_function(create_new_xlsx_file_1)
        .unique("2")
        .initialize();

    test_runner.assert_eq();
    test_runner.cleanup();
}

#[test]
fn dataframe_write_excel02_2() {
    let test_runner = common::TestRunner::new()
        .set_name("dataframe02")
        .set_function(create_new_xlsx_file_2)
        .unique("3")
        .initialize();

    test_runner.assert_eq();
    test_runner.cleanup();
}
